import{_ as l,o as i,c as r,e as a}from"./app.80306d75.js";const e={},o=a('<h1 id="\u4F2F\u6069\u65AF\u65B0\u60C5\u7EEA\u7597\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u4F2F\u6069\u65AF\u65B0\u60C5\u7EEA\u7597\u6CD5" aria-hidden="true">#</a> \u4F2F\u6069\u65AF\u65B0\u60C5\u7EEA\u7597\u6CD5</h1><h2 id="\u8BA4\u77E5\u7597\u6CD5\u548C\u60C5\u7EEA\u6291\u90C1" tabindex="-1"><a class="header-anchor" href="#\u8BA4\u77E5\u7597\u6CD5\u548C\u60C5\u7EEA\u6291\u90C1" aria-hidden="true">#</a> \u8BA4\u77E5\u7597\u6CD5\u548C\u60C5\u7EEA\u6291\u90C1</h2><p>\u5341\u5927\u8BA4\u77E5\u626D\u66F2</p><ol><li>\u975E\u6B64\u5373\u5F7C\u601D\u7EF4</li><li>\u4EE5\u504F\u6982\u5168</li><li>\u5FC3\u7406\u8FC7\u6EE4</li><li>\u5426\u5B9A\u6B63\u9762\u601D\u8003</li><li>\u5984\u4E0B\u7ED3\u8BBA</li><li>\u653E\u5927\u548C\u7F29\u5C0F</li><li>\u60C5\u7EEA\u5316\u63A8\u7406</li><li>\u201C\u5E94\u8BE5\u201D\u53E5\u5F0F</li><li>\u4E71\u8D34\u6807\u7B7E</li><li>\u7F6A\u8D23\u5F52\u5DF1</li></ol><h2 id="\u5B9E\u9645\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u9645\u5E94\u7528" aria-hidden="true">#</a> \u5B9E\u9645\u5E94\u7528</h2><ul><li>\u5EFA\u7ACB\u81EA\u5C0A\uFF0C\u514B\u670D\u65E0\u4EF7\u503C\u611F</li></ul><ol><li>\u53CD\u9A73\u81EA\u6211\u6279\u8BC4\uFF1A \u8BC6\u522B\u4E0B\u610F\u8BC6\u7684\u81EA\u6211\u6279\u8BC4\u601D\u7EF4\uFF0C\u5206\u6790\u5176\u4E2D\u7684\u8BA4\u77E5\uFF0C\u626D\u66F2\u8FDB\u884C\u7406\u6027\u7684\u56DE\u5E94\uFF0C\u81EA\u6211\u8FA9\u62A4\u3002</li><li>\u5FC3\u7406\u751F\u7269\u53CD\u9988\uFF1A\u5BF9\u6D88\u6781\u601D\u7EF4\u8FDB\u884C\u6280\u672F\u76D1\u63A7\u3002</li><li>\u884C\u52A8\u8D77\u6765\uFF0C\u4E0D\u8981\u81EA\u6028\u81EA\u827E\uFF1A \u786E\u5B9A\u884C\u52A8\u8BA1\u5212\uFF0C\u89E3\u51B3\u771F\u6B63\u7684\u95EE\u9898\u3002</li></ol><ul><li>\u6218\u80DC\u65E0\u4E3A\u4E3B\u4E49(\u62D6\u5EF6\u75C7)</li></ul><p>\u884C\u4E8B\u65B9\u5F0F\u4E00\u65E6\u6539\u53D8\uFF0C\u601D\u7EF4\u65B9\u5F0F\u4E5F\u4F1A\u968F\u4E4B\u53D1\u751F\u8F6C\u53D8\uFF0C\u4ECE\u800C\u6709\u6548\u63D0\u5347\u60C5\u7EEA\u3002 \u5BFC\u81F4\u61D2\u6563\u884C\u4E3A\u548C\u65E0\u4E3A\u4E3B\u4E49\u768413\u79CD\u601D\u7EF4\u5B9A\u5F0F\uFF1A</p><ol><li>\u7EDD\u671B\uFF1B</li><li>\u65E0\u52A9(\u4E0D\u505A\u8BA9\u81EA\u5DF1\u9AD8\u5174\u7684\u4E8B\u60C5)\uFF1B</li><li>\u51FB\u8D25\u81EA\u5DF1(\u5C06\u4EFB\u52A1\u65E0\u9650\u653E\u5927\uFF0C\u96BE\u7684\u6CA1\u6CD5\u5904\u7406)\uFF1B</li><li>\u5984\u4E0B\u7ED3\u8BBA</li><li>\u7ED9\u81EA\u5DF1\u8D34\u6807\u7B7E</li><li>\u8F7B\u89C6\u56DE\u62A5(\u4E27\u5931\u4F53\u9A8C\u6EE1\u8DB3\u548C\u5FEB\u4E50\u7684\u80FD\u529B)</li><li>\u5B8C\u7F8E\u4E3B\u4E49</li><li>\u5BB3\u6015\u5931\u8D25\uFF1A\u4EE5\u504F\u6982\u5168\uFF0C\u60DF\u7ED3\u679C\u8BBA</li><li>\u5BB3\u6015\u6210\u529F\uFF1A\u5BB3\u6015\u5931\u53BB\u80DC\u5229\u7684\u679C\u5B9E\uFF0C\u5BB3\u6015\u6210\u529F\u65E0\u6CD5\u6EE1\u8DB3\u4ED6\u4EBA\u7684\u671F\u671B</li><li>\u5BB3\u6015\u53CD\u5BF9\u548C\u6279\u8BC4</li><li>\u5F3A\u8FEB\u548C\u6028\u6068\uFF1A\u901A\u8FC7\u81EA\u5DF1\u5E94\u8BE5\u5B8C\u6210\u67D0\u9879\u4EFB\u52A1\uFF0C\u4F46\u56E0\u4E3A\u538B\u529B\u7B49\u539F\u56E0\u800C\u5FC3\u751F\u6028\u6068\u3002\u56E0\u4E3A\u65E0\u6CD5\u5B8C\u6210\u4EFB\u52A1\uFF0C\u6700\u540E\u5FC3\u751F\u6127\u759A</li><li>\u6297\u632B\u6298\u80FD\u529B\u4F4E\uFF1A \u8BA9\u73B0\u5B9E\u4E0E\u5934\u8111\u4E2D\u7684\u7406\u60F3\u4E16\u754C\u76F8\u63D0\u5E76\u8BBA\uFF0C\u5F53\u4E8C\u8005\u4E0D\u5BF9\u7B49\u65F6\uFF0C\u4F1A\u8C34\u8D23\u73B0\u5B9E\u3002\u6539\u53D8\u7684\u65B9\u6CD5\u5373\u662F\u964D\u4F4E\u671F\u671B\u503C</li><li>\u5185\u759A\u4E0E\u81EA\u8D23\uFF1A \u56FA\u6267\u7684\u8BA4\u4E3A\u81EA\u5DF1\u4E0D\u597D\uFF0C\u5931\u53BB\u6539\u53D8\u7684\u52A8\u529B\u3002</li></ol><p>\u4F7F\u7528\uFF0C\u6BCF\u65E5\u6D3B\u52A8\u8BA1\u5212\u8868\u3001\u53CD\u62D6\u5EF6\u8868\u3001\u6D88\u6781\u601D\u7EF4\u65E5\u5FD7\u3001\u5FEB\u4E50\u9884\u6D4B\u8868\u6765\u8F85\u52A9\u81EA\u5DF1\u884C\u52A8\u8D77\u6765</p><p>\u53CD\u9A73\u201C\u4F46\u662F\u201D<br> \u5B66\u4F1A\u8BA4\u540C\u81EA\u5DF1<br> TIC-TOC\u8868(\u4EFB\u52A1\u5E72\u6270\u578B\u8BA4\u77E5\u8F6C\u6362\u4E3A\u4EFB\u52A1\u5BFC\u5411\u578B\u8BA4\u77E5)<br> \u5316\u96F6\u4E3A\u6574\uFF1A \u5C06\u4EFB\u52A1\u5206\u89E3\u6210\u82E5\u5E72\u96F6\u6563\u7684\u90E8\u5206\uFF0C\u8BBE\u5B9A\u65F6\u95F4\u5B8C\u6210\u5176\u4E2D\u67D0\u4E2A\u90E8\u5206\uFF0C\u5230\u65F6\u95F4\u70B9\u540E\u65E0\u8BBA\u662F\u5426\u5B8C\u6210\u9A6C\u4E0A\u7ED3\u675F\u4EFB\u52A1\u3002\u53BB\u505A\u81EA\u5DF1\u559C\u6B22\u7684\u4E8B<br> \u81EA\u6211\u63A8\u52A8\uFF0C\u62D2\u7EDD\u5F3A\u8FEB\uFF1A\u5C06\u201C\u6211\u5E94\u8BE5\u201D\u6539\u6210\u201C\u6211\u60F3\u8981\u201D<br> \u89E3\u9664\u6212\u5907\u6CD5\uFF1A \u5F53\u88AB\u903C\u505A\u7740\u5BF9\u81EA\u5DF1\u6709\u5229\u7684\u4E8B\u60C5\u7684\u65F6\u5019\uFF0C\u89E3\u9664\u6212\u5907\uFF0C\u5E76\u4E0D\u5C06\u5176\u5F53\u4F5C\u51FA\u5C48\u4ECE\u547D\u4EE4\uFF0C\u800C\u662F\u81EA\u6211\u5148\u524D\u5DF2\u7ECF\u4E86\u76F8\u540C\u7684\u51B3\u5B9A\u3002<br> \u89C6\u89C9\u5316\u6210\u529F\uFF1A\u60F3\u8C61\u6210\u529F\u7684\u6837\u5B50\uFF0C\u79EF\u6781\u6697\u793A\u3002<br> \u8BB0\u5F55\u79EF\u6781\u7684\u4E00\u9762<br> \u9A8C\u8BC1\u4F60\u7684\u201C\u4E0D\u884C\u201D<br> \u201C\u4E0D\u80FD\u8F93\u201D\u65B9\u6CD5\uFF1A\u901A\u8FC7\u9884\u8BBE\u5931\u8D25\u7684\u7ED3\u5C40\u5E76\u8F85\u4EE5\u5E94\u5BF9\u63AA\u65BD\uFF0C\u89C4\u907F\u6050\u60E7<br> \u8981\u5206\u6E05\u5148\u540E\u987A\u5E8F\uFF1A\u5148\u6709\u884C\u52A8\uFF0C\u624D\u4F1A\u4EA7\u751F\u52A8\u529B</p><ul><li>\u8BED\u8A00\u67D4\u9053</li></ul><p>\u4E86\u89E3\u601D\u7EF4\u8FC7\u7A0B\uFF0C\u7406\u6027\u5206\u6790\u6D88\u6781\u601D\u7EF4\u4E2D\u7684\u9519\u8BEF\u6307\u51FA\uFF0C\u5E76\u505A\u51FA\u7406\u6027\u56DE\u5E94</p><ol><li>\u79FB\u60C5\uFF1A \u53CD\u590D\u8FFD\u95EE\u7EC6\u8282\uFF0C\u627E\u51FA\u6279\u8BC4\u8005\u7684\u771F\u6B63\u610F\u56FE\uFF0C\u5C06\u653B\u51FB\u9632\u5B88\u578B\u4EA4\u6D41\u8F6C\u5316\u4E3A\u76F8\u4E92\u5C0A\u91CD\u7684\u5408\u4F5C\u5173\u7CFB\u3002</li><li>\u6D88\u9664\u6279\u8BC4\u8005\u7684\u654C\u610F\uFF1A\u6C9F\u901A\u65F6\u505A\u5230\u8BA4\u540C, \u4E0D\u6316\u82E6\u4E89\u8FA9, \u5B9E\u4E8B\u6C42\u662F</li><li>\u53CD\u9988\u548C\u534F\u5546</li></ol><p>\u603B\u7684\u6765\u8BF4\u56DE\u5E94\u6279\u8BC4\u6709\u4E09\u79CD\u601D\u8DEF\uFF1A \u201C\u60B2\u4F24\u201D\uFF0C \u201C\u75AF\u72C2(\u6124\u6012)\u201D\uFF0C \u201C\u4E50\u89C2\u201D\uFF0C \u800C\u65E0\u7591\u4E50\u89C2\u662F\u6700\u597D\u65B9\u6CD5</p><ul><li>\u751F\u6C14\u4E86\u5417</li></ul><p>\u4F55\u65F6\u8BE5\u6124\u6012\u7684\u601D\u8003\u6807\u51C6<br> \u8BA9\u6211\u6124\u6012\u7684\u8FD9\u4E2A\u4EBA\u4F24\u5BB3\u6211\u662F\u4E0D\u662F\u5B58\u5FC3\u7684\u6545\u610F\u7684\uFF0C\u4ED6\u662F\u4E0D\u662F\u975E\u4F24\u5BB3\u6211\u4E0D\u80AF\u3002<br> \u6211\u751F\u6C14\u6709\u7528\u5417\uFF1F\u4ED6\u80FD\u5E2E\u6211\u5B9E\u73B0\u9884\u671F\u76EE\u6807\u5417\uFF1F\u8FD8\u662F\u53EA\u4F1A\u6253\u51FB\u6211\u3002</p><ol><li>\u6FC0\u53D1\u6B32\u671B</li><li>\u51B7\u5374\u6124\u6012\u7684\u60F3\u6CD5</li><li>\u60F3\u50CF\u66FF\u6362\u6CD5</li><li>\u91CD\u5199\u89C4\u5219\uFF1A\u5C06\u5E94\u8BE5\u6362\u6210\u5982\u679C\xB7\xB7\xB7\u4F1A\u66F4\u597D\uFF0C\u5982\u4ED8\u51FA\u5E76\u4E0D\u603B\u610F\u5473\u7740\u5E94\u8BE5\u5F97\u5230\u56DE\u62A5</li><li>\u5B66\u4F1A\u5BBD\u5BB9</li><li>\u660E\u667A\u7684\u63A7\u5236\u624B\u6BB5</li><li>\u51CF\u5C11\u5E94\u8BE5</li><li>\u8C08\u5224\u7B56\u7565\uFF1A\u5938\u8D5E --&gt; \u63D0\u8981\u6C42 --&gt; \u6D88\u9664\u654C\u610F --&gt; \u91CD\u7533\u8981\u6C42 --&gt; \u4E07\u4E0D\u5F97\u5DF2\u6700\u540E\u901A\u7252</li><li>\u51C6\u786E\u7684\u79FB\u60C5\uFF1A\u5373\u6362\u4F4D\u601D\u8003\uFF0C\u8BD5\u56FE\u4ECE\u5BF9\u65B9\u7684\u89D2\u5EA6\u53BB\u7406\u89E3\u5BF9\u65B9\u7684\u884C\u4E3A</li><li>\u6574\u7406\u601D\u8DEF\uFF0C\u8BA4\u77E5\u9884\u6F14</li></ol><ul><li>\u6218\u80DC\u5185\u759A\u7684\u65B9\u6CD5</li></ul><p>\u5F15\u53D1\u5185\u759A\u5FC3\u7406\u53EF\u80FD\u662F\u8BA4\u4E3A\u81EA\u5DF1\u505A\u9519\u4E86\u4E8B\uFF0C\u4E71\u8D34\u6807\u7B7E\u3001\u7F6A\u8D23\u5F52\u5DF1\uFF0C\u201C\u5E94\u8BE5\u201D\u53E5\u5F0F<br> \u514B\u670D\u5185\u759A\u76846\u4E2A\u65B9\u6CD5</p><ol><li>\u6D88\u6781\u601D\u7EF4\u65E5\u5FD7</li><li>\u6D88\u9664\u201C\u5E94\u8BE5\u201D\u6CD5</li><li>\u5B66\u4F1A\u575A\u6301\u539F\u5219</li><li>\u53CD\u54ED\u8BC9\u6CD5</li><li>\u83AB\u745E\u53CD\u54ED\u8BC9\u6CD5</li><li>\u6539\u53D8\u5FC3\u6001</li></ol><h2 id="\u73B0\u5B9E\u6291\u90C1" tabindex="-1"><a class="header-anchor" href="#\u73B0\u5B9E\u6291\u90C1" aria-hidden="true">#</a> \u73B0\u5B9E\u6291\u90C1</h2><ul><li>\u60B2\u4F24\u4E0D\u7B49\u4E8E\u6291\u90C1</li></ul><p>\u60B2\u4F24\u5E76\u4E0D\u603B\u662F\u4F34\u968F\u7740\u75DB\u82E6\uFF0C\u800C\u6291\u90C1\u662F\u8BA4\u77E5\u626D\u66F2\u7684\u7ED3\u679C\u3002\u6539\u53D8\u8BA4\u77E5\uFF0C\u4E50\u89C2\u751F\u6D3B</p><h2 id="\u9884\u9632\u63AA\u65BD\u548C\u4E2A\u4EBA\u6210\u957F" tabindex="-1"><a class="header-anchor" href="#\u9884\u9632\u63AA\u65BD\u548C\u4E2A\u4EBA\u6210\u957F" aria-hidden="true">#</a> \u9884\u9632\u63AA\u65BD\u548C\u4E2A\u4EBA\u6210\u957F</h2><ul><li>\u6291\u90C1\u75C7\u7684\u6839\u6E90</li></ul><p>\u5FC3\u5E95\u6F5C\u85CF\u7740\u7684\u4E00\u4E9B\u9690\u6027\u5047\u8BBE\u662F\u6291\u90C1\u75C7\u53CD\u590D\u7684\u6839\u6E90</p><p>\u6DF1\u6316\u9690\u6027\u5047\u8BBE\u7684\u65B9\u6CD5\uFF1A</p><ol><li>\u5782\u76F4\u7BAD\u5934\u601D\u7EF4\u5206\u6790\u6CD5</li><li>\u529F\u80FD\u5931\u8C03\u6027\u6001\u5EA6\u95EE\u5377</li></ol><ul><li>\u8BA4\u540C\u4E0A\u763E\u75C7(\u9690\u6027\u5047\u8BBE1\uFF1A\u65E0\u4EBA\u8BA4\u540C\u5219\u60C5\u7EEA\u4F4E\u843D\uFF0C\u88AB\u8BA4\u540C\u5219\u60C5\u7EEA\u9AD8\u6DA8)</li></ul><p>\u8BA4\u540C\u4E0D\u7B49\u4E8E\u4EF7\u503C\uFF0C\u8BA4\u540C\u4E0A\u763E\u75C7\u7684\u6839\u6E90\u5927\u6982\u662F\u5728\u7AE5\u5E74\u7684\u4EB2\u5BC6\u5173\u7CFB\u4E2D\u57CB\u4E0B\u7684</p><p>\u5EFA\u7ACB\u72EC\u7ACB\u81EA\u5C0A\u7684\u65B9\u6CD5:\u6210\u672C-\u6536\u76CA\u5206\u6790 --&gt; \u91CD\u5199\u5047\u8BBE --&gt; \u81EA\u5C0A\u84DD\u56FE \u53E3\u5934\u6280\u5DE7\u548C\u53CD\u62D2\u7EDD\u6280\u5DE7</p><ul><li>\u201C\u7231\u201D\u4E0A\u763E\u75C7(\u9690\u6027\u5047\u8BBE2:\u6CA1\u6709\u771F\u7231\u5C31\u6CA1\u6709\u771F\u6B63\u7684\u5FEB\u4E50)</li></ul><p>\u574F\u5904\uFF1A\u7231\u4F1A\u6C42\u800C\u4E0D\u5F97\uFF0C\u53EA\u4F1A\u8BA9\u81EA\u5DF1\u8D8A\u6765\u8D8A\u5B64\u7ACB</p><ul><li>\u5DE5\u4F5C\u4E0D\u7B49\u4E8E\u4E2A\u4EBA\u4EF7\u503C(\u9690\u6027\u5047\u8BBE3:\u4E2A\u4EBA\u4EF7\u503C\u53D6\u51B3\u4E8E\u5B9E\u9645\u751F\u6D3B\u6240\u53D6\u5F97\u7684\u6210\u5C31)</li></ul><p>\u5DE5\u4F5C\u3001\u6210\u5C31\uFF0C\u5747\u4E0D\u80FD\u5B9A\u4E49\u4E2A\u4EBA\u4EF7\u503C</p><ol><li>\u4E2A\u4EBA\u4EF7\u503C\u53EA\u662F\u4E00\u4E2A\u62BD\u8C61\u7684\u6982\u5FF5\uFF0C\u5B83\u5E76\u4E0D\u5B58\u5728\uFF0C\u6B63\u5982\u751F\u6D3B\u672C\u5C31\u6CA1\u6709\u610F\u4E49\u3002</li><li>\u6BCF\u4E2A\u4EBA\u4ECE\u751F\u5230\u6B7B\u53EA\u6709\u4E00\u4E2A\u4EF7\u503C\u5355\u4F4D\uFF0C\u5E76\u4E14\u90FD\u662F\u4E00\u6837\u7684\uFF0C\u6CA1\u6709\u5FC5\u8981\u53BB\u4E89\u53D6\u4ED6\uFF0C\u4E5F\u6CA1\u6709\u5FC5\u8981\u53BB\u60F3\u7740\u4ED6\u3002</li><li>\u53EA\u6709\u4E00\u79CD\u65B9\u6CD5\u4F1A\u4F7F\u4F60\u5931\u53BB\u81EA\u6211\u4EF7\u503C\u611F\uFF0C\u90A3\u5C31\u662F\u7528\u8352\u8C2C\u4E0D\u53EF\u903B\u8F91\u7684\u6D88\u6781\u601D\u7EF4\u8FEB\u5BB3\u81EA\u5DF1\u3002</li><li>\u53EF\u4EE5\u5C06\u81EA\u5C0A\u770B\u4F5C\u662F\u4E00\u79CD\u51B3\u5FC3\uFF0C\u51B3\u5B9A\u628A\u81EA\u5DF1\u5F53\u505A\u6700\u656C\u91CD\u7684\u4EBA\u4E00\u6837\u7231\u62A4\u3002</li></ol><p>\u9003\u79BB\u6210\u5C31\u7684\u9677\u9631\uFF1A\u53CD\u9A73\u626D\u66F2\u7684\u601D\u7EF4\uFF0C\u7231\u4F60\u6240\u7231</p><ul><li>\u6562\u4E8E\u5E73\u51E1--\u6218\u80DC\u5B8C\u7F8E\u4E3B\u4E49\u7684\u5FC5\u6740\u6280</li></ul><p>\u6218\u80DC\u5B8C\u7F8E\u4E3B\u4E4915\u6CD5</p><h3 id="\u6218\u80DC\u5B8C\u7F8E\u4E3B\u4E49\u768415\u79CD\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u6218\u80DC\u5B8C\u7F8E\u4E3B\u4E49\u768415\u79CD\u65B9\u6CD5" aria-hidden="true">#</a> \u6218\u80DC\u5B8C\u7F8E\u4E3B\u4E49\u768415\u79CD\u65B9\u6CD5\uFF1A</h3><ol><li><strong>\u4ECE\u52A8\u673A\u7740\u624B</strong>\uFF1A\u7406\u89E3\u8FFD\u6C42\u5B8C\u7F8E\u7684\u6839\u672C\u52A8\u673A\u3002</li><li><strong>\u5206\u6790\u5229\u5F0A</strong>\uFF1A\u8BC4\u4F30\u8FFD\u6C42\u5B8C\u7F8E\u7684\u597D\u5904\u548C\u574F\u5904\u3002</li><li><strong>\u53CD\u5B8C\u7F8E\u4E3B\u4E49\u8868</strong>\uFF1A\u8BB0\u5F55\u548C\u53CD\u601D\u5B8C\u7F8E\u4E3B\u4E49\u884C\u4E3A\u3002</li><li><strong>\u63A5\u53D7\u4E0D\u5B8C\u7F8E</strong>\uFF1A\u8BA4\u8BC6\u5230\u5B8C\u7F8E\u7684\u4E8B\u7269\u662F\u4E0D\u5B58\u5728\u7684\u3002</li><li><strong>\u6218\u80DC\u6050\u60E7</strong>\uFF1A\u9762\u5BF9\u548C\u514B\u670D\u5BF9\u4E0D\u5B8C\u7F8E\u7684\u6050\u60E7\u3002</li><li><strong>\u91CD\u89C6\u8FC7\u7A0B</strong>\uFF1A\u4E13\u6CE8\u4E8E\u8FC7\u7A0B\u800C\u975E\u7ED3\u679C\u3002</li><li><strong>\u627F\u62C5\u8D23\u4EFB</strong>\uFF1A\u52C7\u6562\u627F\u62C5\u751F\u6D3B\u4E2D\u7684\u8D23\u4EFB\u3002</li><li><strong>\u5B66\u4F1A\u72AF\u9519</strong>\uFF1A\u63A5\u53D7\u5E76\u4ECE\u9519\u8BEF\u4E2D\u5B66\u4E60\u3002</li><li><strong>\u805A\u7126\u957F\u5904</strong>\uFF1A\u907F\u514D\u8FC7\u5206\u5173\u6CE8\u81EA\u5DF1\u7684\u77ED\u5904\u3002</li><li><strong>\u514B\u670D\u6781\u7AEF\u601D\u7EF4</strong>\uFF1A\u907F\u514D\u201C\u975E\u6B64\u5373\u5F7C\u201D\u7684\u601D\u7EF4\u65B9\u5F0F\u3002</li><li><strong>\u8868\u8FBE\u611F\u53D7</strong>\uFF1A\u52C7\u4E8E\u5410\u9732\u5FC3\u58F0\uFF0C\u91CA\u653E\u538B\u529B\u3002</li><li><strong>\u60F3\u8C61\u5FEB\u4E50\u65F6\u5149</strong>\uFF1A\u56DE\u5FC6\u751F\u547D\u4E2D\u7684\u5FEB\u4E50\u65F6\u5149\u4EE5\u7F13\u89E3\u538B\u529B\u3002</li><li><strong>\u964D\u4F4E\u6807\u51C6</strong>\uFF1A\u9002\u5F53\u964D\u4F4E\u5BF9\u81EA\u5DF1\u7684\u8981\u6C42\u3002</li><li><strong>\u4EBA\u65E0\u5B8C\u4EBA</strong>\uFF1A\u7406\u89E3\u201C\u662F\u4EBA\u5C31\u4F1A\u72AF\u9519\u201D\u7684\u9053\u7406\u3002</li></ol><h2 id="\u6218\u80DC\u7EDD\u671B-\u62D2\u7EDD\u81EA\u6740" tabindex="-1"><a class="header-anchor" href="#\u6218\u80DC\u7EDD\u671B-\u62D2\u7EDD\u81EA\u6740" aria-hidden="true">#</a> \u6218\u80DC\u7EDD\u671B\uFF0C\u62D2\u7EDD\u81EA\u6740</h2><ul><li>\u6700\u540E\u7684\u80DC\u5229--\u9009\u62E9\u6D3B\u4E0B\u6765</li></ul><p>\u81EA\u6740\u7684\u60F3\u6CD5\u51E0\u4E4E\u4EBA\u4EBA\u90FD\u6709</p><p>\u8BC4\u4F30\u81EA\u6740\u51B2\u52A8\uFF1A</p><ol><li>\u4E25\u91CD\u6291\u90C1\uFF0C\u603B\u662F\u611F\u5230\u7EDD\u671B</li><li>\u6709\u81EA\u6740\u53F2</li><li>\u6709\u5177\u4F53\u7684\u81EA\u6740\u8BA1\u5212\uFF0C\u4E14\u5DF2\u505A\u597D\u51C6\u5907</li><li>\u6CA1\u6709\u4EFB\u4F55\u56E0\u7D20\u53EF\u4EE5\u963B\u6B62\u4F60\u81EA\u6740</li></ol><h2 id="\u5E94\u5BF9\u65E5\u5E38\u751F\u6D3B\u4E2D\u7684\u538B\u529B" tabindex="-1"><a class="header-anchor" href="#\u5E94\u5BF9\u65E5\u5E38\u751F\u6D3B\u4E2D\u7684\u538B\u529B" aria-hidden="true">#</a> \u5E94\u5BF9\u65E5\u5E38\u751F\u6D3B\u4E2D\u7684\u538B\u529B</h2><ul><li>\u6211\u662F\u5982\u4F55\u4EE5\u8EAB\u4F5C\u5219\u7684</li></ul><p>\u8BB2\u8FF0\u4F5C\u8005\u81EA\u5DF1\u4F5C\u4E3A\u5FC3\u7406\u5B66\u533B\u751F\u7684\u538B\u529B\uFF0C\u4EE5\u53CA\u5E94\u5BF9\u65B9\u5F0F\u65B9\u6CD5</p><h2 id="\u60C5\u7EEA\u5316\u5B66" tabindex="-1"><a class="header-anchor" href="#\u60C5\u7EEA\u5316\u5B66" aria-hidden="true">#</a> \u60C5\u7EEA\u5316\u5B66</h2><p>\u672C\u90E8\u5206\u524D\u4E24\u7AE0\u8BB2\u8FF0\u4E86\uFF0C\u6291\u90C1\u75C7\u836F\u7269\u548C\u5FC3\u7406\u6CBB\u7597\u4E4B\u95F4\u7684\u5173\u7CFB\uFF0C \u5E76\u5E2E\u52A9\u60A3\u8005\u514B\u670D\u6CBB\u7597\u7F9E\u803B\u611F<br> \u6700\u540E\u4E24\u7AE0\u8BB2\u89E3\u4E86\u6291\u90C1\u75C7\u836F\u7269\u5B9E\u64CD\u6CE8\u610F\u4E8B\u9879\uFF0C \u666E\u901A\u8BFB\u8005\u4E0D\u611F\u5174\u8DA3\u7684\u8BDD\u53EF\u4EE5\u8DF3\u8FC7\u8FD9\u4E24\u7AE0\uFF0C \u6291\u90C1\u75C7\u60A3\u8005\u53EF\u4EE5\u901A\u8FC7\u8FD9\u4E24\u7AE0\u4E86\u89E3\u836F\u7269\u57FA\u672C\u5E38\u8BC6\uFF0C \u5177\u4F53\u7528\u836F\u8FD8\u662F\u8981\u53BB\u6B63\u89C4\u533B\u9662\uFF0C \u9075\u5FAA\u533B\u751F\u5F00\u51FA\u7684\u5904\u65B9\u548C\u6CBB\u7597\u65B9\u6848</p><ul><li>\u5BFB\u627E\u9ED1\u80C6\u6C41</li><li>\u8EAB\u5FC3\u95EE\u9898</li><li>\u5E38\u7528\u6297\u6291\u90C1\u836F\u4F7F\u7528\u5E38\u8BC6</li><li>\u6297\u6291\u90C1\u836F\u7269\u6D88\u8D39\u5B8C\u5168\u6307\u5357</li></ul>',54),t=[o];function n(s,h){return i(),r("div",null,t)}const p=l(e,[["render",n],["__file","\u4F2F\u6069\u65AF\u65B0\u60C5\u7EEA\u7597\u6CD5.html.vue"]]);export{p as default};
