import{_ as l,o as i,c as r,e as p}from"./app.80306d75.js";const a={},u=p('<h1 id="\u4F9D\u604B\u7684\u5F62\u6210\u8BFB\u4E66\u7B14\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u4F9D\u604B\u7684\u5F62\u6210\u8BFB\u4E66\u7B14\u8BB0" aria-hidden="true">#</a> \u4F9D\u604B\u7684\u5F62\u6210\u8BFB\u4E66\u7B14\u8BB0</h1><h2 id="\u7B2C\u4E00\u90E8\u5206-\u5B69\u5B50\u9700\u8981\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u5B69\u5B50\u9700\u8981\u4EC0\u4E48" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206 \u5B69\u5B50\u9700\u8981\u4EC0\u4E48</h2><ul><li>\u7B2C\u4E00\u7AE0\uFF1A \u6BCD\u7231\uFF1A \u6700\u574F\u7684\u60C5\u51B5</li></ul><p>\u63D0\u51FA\u5B69\u5B50\u9700\u8981\u6BCD\u7231\u7684\u89C2\u70B9\uFF0C\u54EA\u6015\u662F\u6709\u4E25\u91CD\u7F3A\u9677\u7684\u6BCD\u7231\uFF0C \u4E5F\u6BD4\u6CA1\u6709\u6BCD\u7231\u5F3A</p><ul><li>\u7B2C\u4E8C\u7AE0\uFF1A \u8D70\u8FDB\u9C8D\u5C14\u6BD4\uFF1A\u63A2\u7A76\u5173\u7CFB\u7406\u8BBA</li></ul><p>\u8BB2\u8FF0\u82F1\u56FD\u7CBE\u795E\u5206\u6790\u5B66\u5BB6\u7EA6\u7FF0\xB7\u9C8D\u5C14\u6BD4\u7684\u6838\u5FC3\u7814\u7A76\u65B9\u5411\uFF0C \u7AE5\u5E74\u7ECF\u5386\u4E0E\u4EB2\u5B50\u5173\u7CFB\u5BF9\u4EBA\u683C\u53D1\u5C55\u9020\u6210\u7684\u5F71\u54CD\uFF0C\u5E76\u5F3A\u8C03\u4E86\u5176\u91CD\u8981\u6027</p><ul><li>\u7B2C\u4E09\u7AE0\uFF1A \u9C8D\u5C14\u6BD4\u548C\u514B\u83B1\u56E0\uFF1A\u5E7B\u60F3\u4E0E\u73B0\u5B9E</li></ul><p>\u8BB2\u8FF0\u7EF4\u4E5F\u7EB3\u7CBE\u795E\u5206\u6790\u5E08\u514B\u83B1\u56E0\u7684\u4E3B\u8981\u7814\u7A76\u65B9\u5411\uFF0C\u65E9\u671F\u5FC3\u7406\u8FC7\u7A0B\u7406\u8BBA\uFF0C\u4FA7\u91CD\u4E8E\u5A74\u513F\u5185\u5FC3\u4E16\u754C\u7684\u5E7B\u60F3\uFF0C\u5373\u66F4\u5173\u6CE8\u4E8E\u6BCD\u5A74\u5173\u7CFB\u7684\u7CBE\u795E\u5C42\u9762\uFF0C\u5E76\u5BF9\u9C8D\u5C14\u6BD4\u4EA7\u751F\u4E86\u4E00\u5B9A\u7684\u5F71\u54CD</p><ul><li>\u7B2C\u56DB\u7AE0\uFF1A \u7CBE\u795E\u75C5\u6001\u8005\u7684\u5F62\u6210\uFF1A44\u4E2A\u5C11\u5E74\u72AF</li></ul><p>\u672C\u7AE0\u63D0\u51FA\u5BBD\u5BB9\u7684\u517B\u80B2\u6C1B\u56F4\uFF0C\u5141\u8BB8\u5B69\u5B50\u76F4\u63A5\u8868\u8FBE\uFF0C\u5F15\u5BFC\u53D1\u6CC4\u66F4\u52A0\u6709\u5229\u4E8E\u5B69\u5B50\u5065\u5EB7\u6210\u957F\uFF0C\u5F62\u6210\u81EA\u63A7\u529B\u3002<br> \u5E76\u4EE544\u4E2A\u76D7\u7A83\u5C11\u5E74\u72AF\u7684\u5171\u6027\u7ECF\u5386\u4F5C\u4E3A\u4F8B\u8BC1\uFF0C \u5373\u4ED6\u4EEC\u90FD\u4E0E\u6BCD\u4EB2\u6709\u8FC7\u4E00\u6BB5\u957F\u65F6\u95F4\u7684\u5265\u593A\u5F0F\u5206\u79BB\u7ECF\u5386(\u5373\u5988\u5988\u79BB\u5F00\u4E86\uFF0C\u4F46\u6CA1\u6709\u4EBA\u63A5\u624B\uFF0C\u6216\u8005\u4E0D\u65AD\u5730\u66F4\u6362\u629A\u80B2\u8005)\uFF0C\u6700\u7EC8\u5F62\u6210\u4E86\u60C5\u611F\u7F3A\u4E4F\u4EBA\u683C\uFF0C\u6781\u5EA6\u51B7\u6F20\u7684\u6027\u683C\u3002<br> \u867D\u7136\u8FD9\u4E9B\u7279\u8D28\u5E76\u4E0D\u4E00\u5B9A\u76F4\u63A5\u5BFC\u81F4\u5B69\u5B50\u72AF\u7F6A\uFF0C\u4F46\u5BF9\u7167\u7EC4\u7684\u5FC3\u7406\u5065\u5EB7\u7684\u5C0F\u5B69\u4E00\u5B9A\u6CA1\u6709\u8FD9\u79CD\u7ECF\u5386\u3002<br> \u603B\u4E4B\u65E9\u671F\u5265\u593A\u5F0F\u7684\u5206\u79BB\u6709\u5BB3\uFF0C\u4EE4\u4EBA\u75DB\u82E6\u5E76\u5F71\u54CD\u4EBA\u683C\u7684\u5F62\u6210<br> \u8FD9\u79CD\u89C2\u70B9\u8BA9\u6211\u8054\u60F3\u5230\u4E86\u4E2D\u56FD\u7684\u7559\u5B88\u513F\u7AE5\u7684\u7ECF\u5386</p><ul><li>\u7B2C\u4E94\u7AE0\uFF1A \u6218\u53F7\u5439\u54CD\uFF1A \u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u62A5\u544A</li></ul><p>\u9C8D\u5C14\u6BD4\u5728\u7814\u7A76\u4E2D\u53D1\u73B0\u6BCD\u7231\u5265\u593A\u513F\u7AE5\u8EAB\u4E0A\u76F8\u4F3C\u7684\u75C7\u72B6\uFF1A\u4EBA\u9645\u4EA4\u5F80\u9677\u4E8E\u8868\u9762\uFF0C\u65E0\u6CD5\u5C06\u5FC3\u6BD4\u5FC3\uFF0C\u96BE\u4EE5\u63A5\u8FD1\uFF0C\u7F3A\u4E4F\u60C5\u7EEA\u56DE\u5E94\uFF0C\u7ECF\u5E38\u65E0\u8C13\u7684\u5077\u548C\u9A97\uFF0C\u4E0A\u8BFE\u6CE8\u610F\u529B\u4E0D\u96C6\u4E2D<br> \u9C8D\u5C14\u6BD4\u8BC1\u660E\u4E86\u6BCD\u5A74\u5173\u7CFB\u7684\u6781\u7AEF\u91CD\u8981\u6027\uFF0C\u5B83\u4E0D\u662F\u4E00\u9879\u798F\u5229\uFF0C\u800C\u662F\u4E00\u9879\u7EDD\u5BF9\u7684\u5FC5\u9700\u54C1<br> \u5E76\u63D0\u51FA\u4E86\u5176\u7814\u7A76\u6838\u5FC3\u6BCD\u7231\u548C\u6BCD\u7231\u5265\u593A\u95EE\u9898</p><ul><li>\u7B2C6\u7AE0\uFF1A\u7B2C1\u6218\u573A\u4E24\u5C81\u513F\u7AE5\u5C31\u533B</li></ul><p>\u672C\u7AE0\u4EE5\u513F\u7AE5\u5C31\u533B\u4E3A\u6848\u4F8B\uFF0C\u5F15\u51FA\u4E86\u7F57\u4F2F\u68EE\u5728\u6BCD\u5A74\u5206\u79BB\u95EE\u9898\u4E0A\u7684\u7814\u7A76\u6210\u679C\u53CA\u5176\u7406\u8BBA\u89C2\u70B9\uFF0C\u5E76\u8BA8\u8BBA\u4E86\u8FD9\u4E9B\u7814\u7A76\u5BF9\u9C8D\u5C14\u6BD4\u7406\u8BBA\u7684\u5F71\u54CD\uFF0C\u63D0\u51FA\u6765\u6BCD\u7231\u5728\u5265\u593A\u8FC7\u7A0B\u4E2D\u5B58\u5728\u53CD\u6297\u3001\u7EDD\u671B\u548C\u758F\u79BB\u8FD9\u4E09\u4E2A\u9636\u6BB5\u7684\u7406\u8BBA\u6846\u67B6\u3002</p><ul><li>\u7B2C7\u7AE0\u5C0F\u9E45\u4E0E\u5C0F\u5B69\u4F9D\u604B\u7406\u8BBA\u7684\u8BDE\u751F</li></ul><p>\u9E2D\u5B50\u521A\u5B75\u5316\u65F6\u4F1A\u8FFD\u968F\u7B2C\u4E00\u4E2A\u770B\u5230\u7684\u79FB\u52A8\u7269\u4F53,\u8FD9\u79CD\u73B0\u8C61\u79F0\u4E3A\u201C\u5370\u968F\u201D\u3002\u4F46\u884C\u4E3A\u6A21\u5F0F\u53D7\u73AF\u5883\u5F71\u54CD\uFF0C\u5982\u679C\u5B75\u5316\u540E\u6CA1\u89C1\u5230\u79FB\u52A8\u7269\u4F53\uFF0C\u5219\u884C\u4E3A\u6A21\u5F0F\u4E0D\u4F1A\u53D1\u751F\uFF0C\u6B63\u5E38\u7684\u60C5\u611F\u7EBD\u5E26\u4E0D\u4F1A\u5F62\u6210\u3002</p><p>\u7C7B\u6BD4\u4E4B\u4E0B\uFF0C\u9C8D\u5C14\u6BD4\u5728\u672C\u7AE0\u5F15\u51FA\u4E86\u4F9D\u604B\u7406\u8BBA\uFF0C\u53CD\u9A73\u4E86\u6BCD\u5A74\u60C5\u611F\u7EBD\u5E26\u4EC5\u7531\u5582\u517B\u548C\u629A\u80B2\u5F62\u6210\u7684\u89C2\u70B9\uFF0C\u6B63\u5F0F\u63D0\u51FA\u201C\u4F9D\u604B\u201D\u6765\u63CF\u8FF0\u6BCD\u5A74\u95F4\u7684\u60C5\u611F\u7EBD\u5E26\u3002\u4F9D\u604B\u7C7B\u4F3C\u7231\u604B\uFF0C\u5B69\u5B50\u6E34\u671B\u4EB2\u8FD1\u6BCD\u4EB2\uFF0C\u82E5\u8FD9\u79CD\u6B32\u671B\u5F97\u4E0D\u5230\u6EE1\u8DB3\u6216\u88AB\u4E2D\u65AD\uFF0C\u4F1A\u5BFC\u81F4\u7126\u8651\u548C\u60B2\u75DB\u3002\u4E2A\u4EBA\u8BA4\u4E3A\u4F9D\u604B\u662F\u5BF9\u5B89\u5168\u611F\u548C\u4FE1\u4EFB\u7684\u6E34\u671B\uFF0C\u662F\u5E7C\u5D3D\u751F\u5B58\u672C\u80FD\u7684\u56FA\u6709\u884C\u4E3A\u6A21\u5F0F\u3002</p><p>\u6B64\u5916\uFF0C\u672C\u7AE0\u9C8D\u5C14\u6BD4\u89E3\u91CA\u4E86\u5206\u79BB\u8FC7\u7A0B\u4E2D\u7684\u4E09\u4E2A\u9636\u6BB5\uFF1A\u53CD\u6297\uFF08\u5206\u79BB\u7126\u8651\uFF09\u3001\u7EDD\u671B\uFF08\u54C0\u4F24\uFF09\u3001\u758F\u79BB\uFF08\u9632\u5FA1\uFF09\u3002</p><ul><li>\u7B2C8\u7AE0 \u201C\u7ED9\u9E45\u505A\u4EC0\u4E48\u7CBE\u795E\u5206\u6790\uFF1F\u201D\uFF1A\u6DF7\u4E71\uFF0C\u654C\u5BF9\u4E0E\u4E89\u8BBA</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u9C8D\u5C14\u6BD4\u6BCD\u7231\u5265\u593A\u7814\u7A76\u6240\u5F15\u53D1\u7684\u4E89\u8BAE\uFF0C\u9762\u4E34\u7740\u514B\u83B1\u56E0\u5B66\u6D3E\u548C\u5F17\u6D1B\u4F0A\u5FB7\u5B66\u6D3E\u7684\u62A8\u51FB\u3002</p><ul><li>\u7B2C9\u7AE0 \u7334\u5B50\u4E4B\u7231\uFF1A\u6E29\u6696\uFF0C\u5B89\u5168\uFF0C\u6301\u7EED\u6027\u3002</li></ul><p>\u672C\u7AE0\u8BB2\u8FF0\u4E86\u54C8\u6D1B\u7B49\u4EBA\u5BF9\u7334\u5B50\u6BCD\u5A74\u5206\u79BB\u7684\u7814\u7A76\uFF0C\u5176\u7ED3\u679C\u589E\u5F3A\u4E86\u4F9D\u604B\u7406\u8BBA\u7684\u53EF\u4FE1\u5EA6\u3002\u540C\u65F6\u5B89\u65AF\u6C83\u65AF\u6307\u51FA\u6BCD\u7231\u5265\u593A\u6709\u4E09\u4E2A\u7EF4\u5EA6\uFF0C\u5206\u522B\u662F\u6BCD\u7231\u7F3A\u5931\u6BCD\u7231\u626D\u66F2\u6BCD\u7231\u4E2D\u65AD\u3002\u8FD9\u4E9B\u5B66\u8005\u7684\u5B9E\u9A8C\u6216\u8005\u7406\u8BBA\uFF0C\u4F7F\u5F97\u4F9D\u604B\u7406\u8BBA\u66F4\u52A0\u5B8C\u5584\uFF0C\u53EF\u4FE1\u5EA6\u66F4\u9AD8\u3002</p><h2 id="\u7B2C\u4E8C\u90E8\u5206-\u7A81\u7834-\u629A\u517B\u65B9\u5F0F\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u7A81\u7834-\u629A\u517B\u65B9\u5F0F\u5206\u6790" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206 \u7A81\u7834\uFF1A\u629A\u517B\u65B9\u5F0F\u5206\u6790</h2><ul><li>\u7B2C10\u7AE0 \u5B89\u65AF\u6C83\u65AF\u5728\u4E4C\u5E72\u8FBE</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u5B89\u65AF\u6C83\u65AF\u5728\u4E4C\u5E72\u8FBE\u7684\u7814\u7A76\uFF0C\u4ED6\u4ECE\u5B89\u5168\u611F\u51FA\u53D1\uFF0C\u63D0\u51FA\u4E86\u5B89\u5168\u57FA\u5730\u7684\u6982\u5FF5\uFF0C\u52A0\u6DF1\u4E86\u6211\u4EEC\u5BF9\u6BCD\u5A74\u7EBD\u5E26\u548C\u4EBA\u9645\u60C5\u611F\u7EBD\u5E26\u7684\u7406\u89E3\u3002</p><ul><li>\u7B2C11\u7AE0 \u964C\u751F\u60C5\u5883\u5B9E\u9A8C</li></ul><p>\u672C\u7AE0\u8BB2\u8FF0\u4E86\u5B89\u65AF\u6C83\u65AF\u5BF9\u5DF4\u5C14\u7684\u6469\u513F\u7AE5\u7684\u7814\u7A76\u3002\u5DF4\u5C14\u7684\u6469\u513F\u7AE5\u7684\u7814\u7A76\u7ED3\u679C\u4E0E\u7518\u8FBE\u513F\u7AE5\u7684\u7814\u7A76\u6781\u4E3A\u76F8\u4F3C\u3002<br> \u968F\u540E\uFF0C\u5B89\u65AF\u6C83\u65AF\u8BBE\u7F6E\u4E86\u964C\u751F\u60C5\u5883\u5B9E\u9A8C\uFF0C\u901A\u8FC7\u8BE5\u5B9E\u9A8C\uFF0C\u5979\u5C06\u6BCD\u4EB2\u7684\u5173\u7231\u5212\u5206\u4E3A\u56DB\u4E2A\u7EF4\u5EA6\uFF1A<br> \u7B2C\u4E00\uFF0C\u6BCD\u4EB2\u5BF9\u5B69\u5B50\u53D1\u51FA\u7684\u4FE1\u53F7\u6709\u591A\u654F\u611F\uFF1B<br> \u7B2C\u4E8C\uFF0C\u6BCD\u4EB2\u5728\u591A\u5927\u7A0B\u5EA6\u4E0A\u63A5\u7EB3\u548C\u62D2\u7EDD\u5B69\u5B50<br> \u7B2C\u4E09\uFF0C\u5728\u5582\u98DF\u3001\u629A\u89E6\u548C\u966A\u4F34\u65F6\uFF0C\u5979\u662F\u8FC1\u5C31\u5B69\u5B50\u7684\u9700\u6C42\u8FD8\u662F\u66F4\u591A\u5730\u5E72\u6D89\uFF0C\u5F3A\u52A0\u5979\u7684\u4E2A\u4EBA\u610F\u613F\u548C\u65E5\u5E38\u5B89\u6392\uFF1B<br> \u7B2C\u56DB\uFF0C\u5979\u662F\u5426\u6709\u7A7A\u7167\u987E\u5B69\u5B50\uFF0C\u5FFD\u89C6\u5B69\u5B50\u9700\u6C42\u7684\u9891\u7387\u5982\u4F55\u3002</p><p>\u5728\u8FD9\u56DB\u4E2A\u7EF4\u5EA6\u4E0A\uFF0C\u5B89\u5168\u578B\u3001\u77DB\u76FE\u578B\u3001\u56DE\u907F\u578B\u548C\u7126\u8651\u578B\u513F\u7AE5\u7684\u6BCD\u4EB2\u8868\u73B0\u51FA\u663E\u8457\u5DEE\u5F02\u3002<br> \u5B69\u5B50\u80FD\u5426\u5F62\u6210\u5B89\u5168\u578B\u4F9D\u604B\u7684\u5173\u952E\u4E0D\u5728\u4E8E\u6BCD\u7231\u5173\u6000\u7684\u91CF\uFF0C\u800C\u5728\u4E8E\u6BCD\u4EB2\u7684\u6E29\u67D4\u3001\u8D34\u5FC3\u548C\u6280\u5DE7\u3002\u5B89\u5168\u578B\u4F9D\u604B\u513F\u7AE5\u7684\u6BCD\u4EB2\u66F4\u5C11\u8BA9\u5B69\u5B50\u7ECF\u5386\u4E0D\u6109\u5FEB\u7684\u4EB2\u8FD1\u4F53\u9A8C\uFF0C\u5F53\u5B69\u5B50\u60F3\u8981\u6BCD\u4EB2\u62B1\u65F6\uFF0C\u5979\u4EEC\u603B\u662F\u6709\u6C42\u5FC5\u5E94\u3002</p><p>\u7136\u800C\uFF0C\u5B89\u65AF\u6C83\u65AF\u5E76\u4E0D\u6EE1\u8DB3\u4E8E\u7B80\u5355\u7684\u5B89\u5168\u578B\u3001\u77DB\u76FE\u578B\u548C\u56DE\u907F\u578B\u5206\u7C7B\uFF0C\u5979\u8BA4\u4E3A\u8FD9\u4E09\u7C7B\u592A\u8FC7\u7B3C\u7EDF\uFF0C\u4E8E\u662F\u5C06\u4E0D\u5B89\u5168\u5927\u7C7B\u5206\u4E3A\u4E24\u4E2A\u5B50\u7C7B\uFF0C\u5C06\u5B89\u5168\u5927\u7C7B\u5206\u4E3A\u56DB\u4E2A\u5B50\u7C7B\u3002\u8BE6\u7EC6\u5185\u5BB9\u89C1\u5B89\u65AF\u6C83\u65AF\u4E0E\u4EBA\u5408\u8457\u7684\u300A\u4F9D\u604B\u6A21\u5F0F\u300B\u4E00\u4E66\u3002</p><ul><li>\u7B2C12\u7AE0 \u7B2C2\u6218\u573A\uFF1A\u5B89\u65AF\u6C83\u65AF\u7684\u7F8E\u56FD\u9769\u547D</li></ul><p>\u5B89\u65AF\u6C83\u65AF\u7684\u7814\u7A76\u5728\u7F8E\u56FD\u5FC3\u7406\u5B66\u754C\u5F15\u8D77\u4E86\u5DE8\u5927\u8F70\u52A8\u3002\u652F\u6301\u8005\u91CD\u590D\u4E86\u5979\u7684\u5B9E\u9A8C\uFF0C\u5206\u6790\u4E86\u5979\u7684\u6570\u636E\uFF0C\u63D0\u51FA\u4E86\u914D\u5408\u4E0E\u5E72\u9884\u7684\u7EF4\u5EA6\u6765\u89E3\u91CA\u964C\u751F\u60C5\u5883\u5B9E\u9A8C\u3002\u8FD8\u7814\u7A76\u4E86\uFF0C\u513F\u7AE5\u7684\u653B\u51FB\u6027\u4E0E\u4F9D\u8D56\u6027\u7EF4\u5EA6\uFF0C\u89E3\u91CA\u4E86\u4F9D\u604B\u4E0E\u4F9D\u8D56\u7684\u533A\u522B\u3002\u968F\u540E\u57FA\u4E8E\u5B69\u5B50\u57281\u5C81\u52301.5\u5C81\u95F4\u7684\u5B9E\u9A8C\u8BC1\u660E\u4E86\u4F9D\u604B\u7C7B\u578B\u65E9\u671F\u4E0D\u4F1A\u968F\u65F6\u95F4\u53D8\u5316\u7684\u7ED3\u8BBA\u3002</p><p>\u884C\u4E3A\u4E3B\u4E49\u5B66\u6D3E\uFF08\u4EE5\u5DF4\u752B\u6D1B\u592B\u7684\u72D7\u7684\u5B9E\u9A8C\u4E3A\u4EE3\u8868\uFF09\u8BA4\u4E3A\u513F\u7AE5\u7684\u53D1\u5C55\u8FC7\u7A0B\u662F\u4E00\u6761\u57FA\u4E8E\u5916\u754C\u73AF\u5883\u5F71\u54CD\u7684\u8FDE\u7EED\u5E73\u6ED1\u7684\u66F2\u7EBF\uFF0C\u5F3A\u8C03\u5B69\u5B50\u7684\u53EF\u5851\u6027\u5F88\u5F3A\uFF0C\u5E76\u8BA4\u4E3A\u5B69\u5B50\u51FA\u751F\u5C31\u77E5\u9053\u8D8B\u5229\u907F\u5BB3\u3002\u4ED6\u4EEC\u5BF9\u5B89\u65AF\u6C83\u65AF\u7684\u7406\u8BBA\u8FDB\u884C\u4E86\u731B\u70C8\u7684\u653B\u51FB\u3002</p><p>\u5728\u652F\u6301\u548C\u653B\u51FB\u4E0B\uFF0C\u5B89\u65AF\u6C83\u65AF\u7684\u7406\u8BBA\u5F97\u5230\u4E86\u957F\u8DB3\u7684\u53D1\u5C55\uFF0C\u5B9E\u9A8C\u786E\u7ACB\u4E86\u4E09\u7C7B\u513F\u7AE5\u884C\u4E3A\u6A21\u5F0F\uFF1A\u5B89\u5168\u578B\u4F9D\u604B\u3001\u56DE\u907F\u578B\u4F9D\u604B\u548C\u77DB\u76FE\u578B\u4F9D\u604B\u3002</p><h2 id="\u7B2C\u4E09\u90E8\u5206-\u65E9\u671F\u4F9D\u604B\u7684\u547D\u8FD0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u65E9\u671F\u4F9D\u604B\u7684\u547D\u8FD0" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206 \u65E9\u671F\u4F9D\u604B\u7684\u547D\u8FD0</h2><ul><li>13\u7AE0 \u660E\u5C3C\u82CF\u8FBE\u7814\u7A76 \u629A\u517B\u65B9\u5F0F\u4E0E\u4EBA\u683C\u53D1\u5C55</li></ul><p>\u82CF\u52B3\u592B\u638C\u63E1\u4E86\u4E00\u7EC4\u5468\u5C81\u513F\u7AE5\u7684\u4F9D\u604B\u60C5\u51B5\uFF0C\u5E76\u7EE7\u7EED\u7814\u7A76\u76F4\u5230\u4ED6\u4EEC\u8FBE\u5230\u5B66\u524D\u5E74\u9F84\u3002<br> \u671F\u95F4\u4ED6\u7814\u7A76\u4E86\u6BCD\u4EB2\u5982\u4F55\u5F71\u54CD\u5B69\u5B50\u7684\u884C\u4E3A\uFF0C\u4EE5\u53CA\u4E0D\u540C\u4F9D\u604B\u7C7B\u578B\u513F\u7AE5\u7684\u884C\u4E3A\u6F14\u5316\u3002<br> \u8FD9\u4E9B\u7814\u7A76\u8868\u660E\uFF0C\u65E9\u671F\u7684\u5B89\u5168\u6027\u4F9D\u604B\u4E0D\u80FD\u4FDD\u8BC1\u5B69\u5B50\u65E5\u540E\u65E0\u5FE7\u65E0\u8651\u3002<br> \u4E0D\u540C\u4F9D\u604B\u7C7B\u578B\u7684\u513F\u7AE5\u5728\u56DB\u4E94\u5C81\u65F6\u4F1A\u8868\u73B0\u51FA\u663E\u8457\u7684\u9002\u5E94\u80FD\u529B\u5DEE\u5F02\u3002<br> \u8BE5\u7814\u7A76\u5C06\u4F9D\u604B\u7406\u8BBA\u5411\u524D\u63A8\u8FDB\u4E86\u4E00\u5927\u6B65\uFF0C\u4F46\u4ECD\u6709\u8BB8\u591A\u672A\u80FD\u89E3\u51B3\u7684\u95EE\u9898\u3002</p><ul><li>\u7B2C14\u7AE0 \u6BCD\u4EB2\uFF0C\u7236\u4EB2\u548C\u5916\u90E8\u4E16\u754C\uFF1A\u4F9D\u604B\u54C1\u8D28\u53CA\u7AE5\u5E74\u5173\u7CFB</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u89C2\u5BDF\u5230\u5B89\u5168\u578B\u4F9D\u604B\u4E0E\u826F\u597D\u4F19\u4F34\u5173\u7CFB\u4E4B\u95F4\u6709\u663E\u8457\u76F8\u5173\u6027\uFF0C\u800C\u5176\u4ED6\u4E0D\u5B89\u5168\u578B\u4F9D\u604B\u7684\u513F\u7AE5\u5728\u4EBA\u9645\u5173\u7CFB\u5904\u7406\u65B9\u9762\u8F83\u5F31\u3002<br> \u540C\u65F6\uFF0C\u5F3A\u8C03\u4E86\u7236\u4EB2\u5BF9\u6BCD\u5A74\u5173\u7CFB\u7684\u5F71\u54CD\u4EE5\u53CA\u5728\u513F\u7AE5\u4F9D\u604B\u7C7B\u578B\u5F62\u6210\u4E2D\u7684\u91CD\u8981\u4F5C\u7528\u3002\u9F13\u52B1\u5176\u4ED6\u5B66\u8005\u7EE7\u7EED\u8DDF\u8E2A\u7814\u7A76\u513F\u7AE5\u957F\u5927\u540E\u7684\u4F9D\u604B\u7C7B\u578B\u53D1\u5C55\u3002</p><ul><li>15\u7AE0 \u5FC3\u667A\u7ED3\u6784\uFF1A \u5EFA\u7ACB\u4EBA\u7C7B\u8054\u7ED3\u7684\u6A21\u578B</li></ul><p>\u9C8D\u5C14\u6BD4\u5728\u672C\u7AE0\u4E2D\u63D0\u51FA\u4E86\u5185\u5728\u5DE5\u4F5C\u6A21\u578B\u7684\u6982\u5FF5\uFF0C\u89E3\u91CA\u4E86\u4E3A\u4EC0\u4E48\u4F9D\u604B\u6A21\u5F0F\u4E00\u65E6\u5F62\u6210\u540E\u5F88\u96BE\u6539\u53D8\uFF0C\u5E76\u5728\u4F17\u591A\u5FC3\u7406\u5B66\u5B66\u672F\u9886\u57DF\u4E4B\u95F4\u5EFA\u7ACB\u4E86\u6865\u6881\u3002</p><ul><li>\u7B2C16\u7AE0 \u9ED1\u7BB1\u91CD\u542F\uFF1A\u6885\u56E0\u7684\u4F2F\u514B\u5229\u7814\u7A76\u3002</li></ul><p>\u6885\u56E0\u5BF9\u6BD4\u4E86\u964C\u751F\u60C5\u5883\u5B9E\u9A8C\u548C6\u5C81\u513F\u7AE5\u7684\u884C\u4E3A\u5206\u6790\u3002\u8FD9\u4E9B\u7814\u7A76\u8BB0\u5F55\u4E0D\u4EC5\u8868\u660E\u4F9D\u604B\u7C7B\u578B\u4F1A\u5728\u4EE3\u9645\u95F4\u4F20\u9012\uFF0C\u8FD8\u89E3\u91CA\u4E86\u4F9D\u604B\u7ECF\u5386\u5982\u4F55\u6700\u7EC8\u878D\u5165\u6210\u5E74\u4EBA\u7684\u5FC3\u667A\u3002</p><ul><li>\u7B2C17\u7AE0 \u63A2\u8EAB\u89E6\u7231\uFF1A\u7126\u8651\u578B\u4F9D\u604B\u513F\u7AE5\u7684\u7B56\u7565\u3001\u9632\u5FA1\u53CA\u6539\u53D8\u7684\u6982\u7387</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u7126\u8651\u578B\u3001\u56DE\u907F\u578B\u548C\u77DB\u76FE\u578B\u8FD9\u4E09\u79CD\u4E0D\u5B89\u5168\u4F9D\u604B\u7C7B\u578B\u513F\u7AE5\u7684\u7279\u70B9\uFF0C\u4EE5\u53CA\u5982\u4F55\u5728\u65E9\u671F\u6EE1\u8DB3\u5176\u4F9D\u604B\u9700\u6C42\u5E76\u7EA0\u6B63\u5176\u5185\u5728\u4F9D\u604B\u6A21\u5F0F\u7684\u65B9\u6CD5\u3002</p><ul><li>\u7B2C18\u7AE0 \u4E0D\u582A\u7684\u9700\u6C42\uFF0C\u4E11\u964B\u7684\u6211\uFF1A\u7126\u8651\u6027\u4F9D\u604B\u4E0E\u7F9E\u803B</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u88AB\u5FFD\u89C6\u3001\u88AB\u60E9\u7F5A\u3001\u88AB\u5632\u7B11\u3001\u88AB\u6253\u538B\u7B49\u60C5\u51B5\u3002\u5F53\u4F9D\u604B\u9700\u6C42\u672A\u88AB\u6EE1\u8DB3\u65F6\uFF0C\u5E7C\u513F\u4F1A\u4EA7\u751F\u7F9E\u803B\u611F\uFF0C\u65E0\u6CD5\u6B63\u89C6\u5185\u5FC3\u7684\u4F9D\u604B\u9700\u6C42\uFF0C\u8FDB\u800C\u53D1\u5C55\u51FA\u81EA\u5351\u3001\u4E0D\u5B89\u5168\u578B\u4F9D\u604B\u7B49\u7279\u8D28\u3002\u4F9D\u604B\u54C1\u8D28\u65E0\u7591\u4F1A\u5F71\u54CD\u4E2A\u4F53\u5728\u6210\u5E74\u540E\u53CA\u906D\u9047\u5371\u673A\u65F6\u5982\u4F55\u5BFB\u6C42\u4EBA\u9645\u5173\u7CFB\u7684\u8054\u7ED3\u3002</p><ul><li>\u7B2C19\u7AE0 \u65B0\u4E00\u4EE3\u53CD\u5BF9\u8005\uFF1A\u7814\u7A76\u7ED3\u679C\u7684\u4E89\u8BAE</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u5B66\u754C\u5404\u65B9\u5BF9\u4F9D\u604B\u7406\u8BBA\u7814\u7A76\u548C\u5B9E\u9A8C\u7684\u8D28\u7591\u4E0E\u653B\u51FB</p><h2 id="\u7B2C\u56DB\u90E8\u5206-\u653E\u7236\u6BCD\u4E00\u9A6C-\u65B0\u4E00\u8F6E\u5148\u5929\u4E0E\u540E\u5929\u4E4B\u4E89" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u90E8\u5206-\u653E\u7236\u6BCD\u4E00\u9A6C-\u65B0\u4E00\u8F6E\u5148\u5929\u4E0E\u540E\u5929\u4E4B\u4E89" aria-hidden="true">#</a> \u7B2C\u56DB\u90E8\u5206\uFF1A\u653E\u7236\u6BCD\u4E00\u9A6C\uFF01\u65B0\u4E00\u8F6E\u5148\u5929\u4E0E\u540E\u5929\u4E4B\u4E89</h2><ul><li>\u7B2C20\u7AE0 \u5929\u751F\u5982\u6B64\uFF1F\u5207\u65AF\u4E0E\u56F0\u96BE\u513F\u7AE5</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u4EE5\u65AF\u7279\u62C9\xB7\u5207\u65AF\u4E3A\u9996\u7684\u5BF9\u513F\u7AE5\u5148\u5929\u6C14\u8D28\u7684\u7814\u7A76\u3002<br> \u4ED6\u4EEC\u5728\u6D3B\u52A8\u6C34\u5E73\u3001\u8282\u5F8B\u6027\u3001\u8D8B\u907F\u6027\u3001\u9002\u5E94\u6027\u3001\u53CD\u5E94\u5F3A\u5EA6\u3001\u53CD\u5E94\u9608\u9650\u3001\u5FC3\u5883\u8D28\u91CF\u3001\u6CE8\u610F\u529B\u5206\u6563\u7A0B\u5EA6\u3001\u6CE8\u610F\u529B\u5E7F\u5EA6\u548C\u6301\u4E45\u6027\u8FD99\u4E2A\u65B9\u9762\u8BC4\u4F30\u5B69\u5B50\u7684\u5148\u5929\u6C14\u8D28\u7279\u70B9\u3002<br> \u5229\u7528\u8FD99\u4E2A\u7EF4\u5EA6\uFF0C\u5C06\u5B69\u5B50\u7684\u5148\u5929\u6C14\u8D28\u5206\u7C7B\u4E3A\u56F0\u96BE\u513F\u7AE5\u3001\u6162\u70ED\u513F\u7AE5\u548C\u968F\u548C\u513F\u7AE5\u4E09\u5927\u7C7B\u3002<br> \u4ED6\u4EEC\u8BA4\u4E3A\u5B69\u5B50\u7684\u5148\u5929\u6C14\u8D28\u4E0E\u540E\u5929\u73AF\u5883\u76F8\u4E92\u4F5C\u7528\u6700\u7EC8\u9020\u5C31\u4E86\u4EBA\u683C\u7279\u5F81\u3002</p><ul><li>\u7B2C21\u7AE0 \u751F\u7269\u51B3\u5B9A\u8BBA\u7684\u590D\u5174\uFF1A\u6C14\u8D28\u4E4B\u8FA9</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u884C\u4E3A\u9057\u4F20\u5B66\u5BF9\u4F9D\u604B\u53D1\u5C55\u7684\u5F71\u54CD\uFF0C\u4F9D\u604B\u5B66\u8005\u548C\u53CD\u5BF9\u6D3E\u5BF9\u6B64\u5C55\u5F00\u4E86\u6FC0\u70C8\u7684\u4E89\u8FA9\uFF0C\u4E5F\u5373\u5148\u5929\u4E0E\u540E\u5929\u4E4B\u4E89\u3002<br> \u6700\u7EC8\u4F9D\u604B\u7406\u8BBA\u5438\u7EB3\u4E86\u884C\u4E3A\u9057\u4F20\u5B66\u7684\u89C2\u70B9\uFF0C\u5E76\u8BA4\u4E3A\u9057\u4F20\u5BF9\u4F9D\u604B\u7C7B\u578B\u7684\u5F62\u6210\u548C\u53D1\u5C55\u4EA7\u751F\u4E86\u4E00\u5B9A\u7684\u5F71\u54CD\uFF0C\u4F46\u4E0D\u662F\u6700\u4E3B\u8981\u7684\u3002</p><ul><li>\u7B2C22\u7AE0 \u6012\u53CA\u6258\u513F\u6240\uFF1A \u5A74\u513F\u65E5\u6258\u4E4B\u6218</li></ul><p>\u672C\u7AE0\u8BA8\u8BBA\u4E86\u5A74\u513F\u65E5\u6258\u5BF9\u4F9D\u604B\u7C7B\u578B\u5F62\u6210\u7684\u5F71\u54CD\uFF0C\u5305\u62EC\u73B0\u4EE3\u5973\u6027\u5728\u5DE5\u4F5C\u4E0E\u629A\u80B2\u513F\u7AE5\u95F4\u7684\u5E73\u8861\u56F0\u5883\u53CA\u5973\u6743\u73B0\u8C61\u3002\u5404\u5B66\u8005\u5BF9\u6B64\u7814\u7A76\u7ED3\u679C\u4E0D\u4E00\u3002\u5973\u6743\u3001\u7236\u4EB2\u517B\u80B2\u89D2\u8272\u53CA\u4E0D\u540C\u653F\u6CBB\u89C2\u70B9\u7684\u878D\u5408\uFF0C\u4F7F\u5A74\u513F\u65E5\u6258\u5BF9\u4F9D\u604B\u7C7B\u578B\u5F62\u6210\u7684\u5F71\u54CD\u8FD9\u4E2A\u95EE\u9898\u53D8\u5F97\u66F4\u590D\u6742\u3002<br> \u603B\u7684\u6765\u8BF4\uFF0C\u5A74\u513F\u4F9D\u604B\u7C7B\u578B\u4E3B\u8981\u53D7\u4E3B\u8981\u629A\u517B\u8005\u7684\u5F71\u54CD\u3002\u65E0\u8BBA\u662F\u6BCD\u4EB2\u3001\u7236\u4EB2\u8FD8\u662F\u4FDD\u59C6\uFF0C\u5EFA\u7ACB\u7A33\u5B9A\u3001\u957F\u4E45\u3001\u4F53\u8D34\u7684\u5173\u7CFB\u624D\u662F\u5173\u952E\u3002</p><ul><li>\u7B2C23\u7AE0 \u60CA\u4EBA\u7684\u8C03\u8C10\uFF1A\u5A74\u513F\u4E0D\u4E3A\u4EBA\u77E5\u7684\u60C5\u7EEA\u751F\u6D3B</li></ul><p>\u672C\u7AE0\u8BB2\u8FF0\u4E86\u8C03\u8C10\u7684\u6982\u5FF5\uFF0C\u5F3A\u8C03\u5A74\u513F\u6709\u4E30\u5BCC\u7684\u5185\u5FC3\u4E16\u754C\u3002\u6BCD\u5A74\u4E92\u52A8\u4E2D\u7684\u60C5\u7EEA\u5171\u9E23\u79F0\u4E3A\u8C03\u8C10\u3002<br> \u7126\u8651\u578B\u4F9D\u604B\u513F\u7AE5\u7684\u6BCD\u4EB2\u9009\u62E9\u6027\u4E0D\u8C03\u8C10\uFF0C\u4F46\u5728\u6050\u60E7\u65F6\u4F1A\u8C03\u8C10\uFF0C\u8FD9\u5C06\u8BAD\u7EC3\u5B69\u5B50\u7528\u6050\u60E7\u5EFA\u7ACB\u60C5\u611F\u8054\u7CFB\u3002<br> \u56DE\u907F\u578B\u4F9D\u604B\u513F\u7AE5\u8868\u8FBE\u8D1F\u9762\u60C5\u7EEA\u65F6\uFF0C\u6BCD\u4EB2\u4E0D\u8C03\u8C10\uFF0C\u4EC5\u5728\u60C5\u7EEA\u9AD8\u6DA8\u65F6\u624D\u8C03\u8C10\u3002<br> \u5B89\u5168\u578B\u4F9D\u604B\u5A74\u513F\u8868\u73B0\u8D1F\u9762\u60C5\u7EEA\u65F6\u4F1A\u5F97\u5230\u63A5\u89E6\u548C\u5B89\u6170\uFF0C\u6BCD\u4EB2\u4E0D\u4E3B\u52A8\u5E72\u6D89\u5176\u73A9\u800D\u3002<br> \u6BCD\u4EB2\u65E0\u9700\u5B8C\u7F8E\uFF0C\u6BCF\u5BF9\u6BCD\u5B50\u627F\u53D7\u4E0D\u5B8C\u7F8E\u7684\u7A0B\u5EA6\u4E0D\u540C\u3002\u5B69\u5B50\u72EC\u7ACB\u65F6\uFF0C\u6BCD\u4EB2\u4E0D\u5E94\u5B8C\u7F8E\u3002\u4F46\u5F53\u8C03\u8C10\u5360\u4E3B\u6D41\u65F6\uFF0C\u6709\u52A9\u5B69\u5B50\u7406\u89E3\u4EB2\u5BC6\u5173\u7CFB\uFF0C\u758F\u901A\u4EBA\u9645\u9694\u9602\u3002</p><h2 id="\u7B2C\u4E94\u90E8\u5206-\u6210\u5E74\u671F\u7684\u4F9D\u604B\u9057\u4EA7" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E94\u90E8\u5206-\u6210\u5E74\u671F\u7684\u4F9D\u604B\u9057\u4EA7" aria-hidden="true">#</a> \u7B2C\u4E94\u90E8\u5206 \u6210\u5E74\u671F\u7684\u4F9D\u604B\u9057\u4EA7</h2><ul><li>\u7B2C24\u7AE0 \u7956\u8F88\u7684\u6B8B\u8FF9\uFF1A \u4F20\u9012\u4E0D\u5B89\u5168\u578B\u4F9D\u604B</li></ul><p>\u672C\u7AE0\u8BB2\u8FF0\u4E86\u4F2F\u514B\u5229\u7684\u7814\u7A76\uFF0C\u8868\u660E\u5A74\u513F\u7684\u4F9D\u604B\u7C7B\u578B\u4E0E\u7236\u6BCD\u7684\u9AD8\u5EA6\u76F8\u5173\uFF0C\u56E0\u4E3A\u5A74\u513F\u4E60\u5F97\u4E86\u4E3B\u8981\u629A\u80B2\u8005\u7684\u884C\u4E3A\u6A21\u5F0F\uFF0C\u5F62\u6210\u76F8\u4F3C\u7684\u4F9D\u604B\u7C7B\u578B\u3002</p><ul><li>\u7B2C25\u7AE0 \u6210\u5E74\u671F\u4F9D\u604B\uFF1A \u5B89\u5168\u57FA\u5730\u4E0E\u7EDD\u671B\u7684\u5185\u5728\u5C0F\u5B69</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u4E0D\u540C\u4F9D\u604B\u7C7B\u578B\u7684\u6210\u5E74\u4EBA\u5BF9\u5B89\u5168\u57FA\u5730\u6982\u5FF5\u7684\u4E0D\u540C\u8BA4\u8BC6\uFF0C\u4EE5\u53CA\u4ED6\u4EEC\u5728\u9762\u5BF9\u4EB2\u5BC6\u5173\u7CFB\u65F6\u7684\u4E0D\u540C\u8868\u73B0\u3002<br> \u56DE\u907F\u578B\u4E3A\u4E3B\u5BFC\u7684\u4EBA\u5426\u8BA4\u5B89\u5168\u57FA\u5730\u7684\u5B58\u5728\u3002\u77DB\u76FE\u578B\u4E3A\u4E3B\u5BFC\u7684\u4EBA\u77E5\u9053\u5B89\u5168\u57FA\u5730\u7684\u5B58\u5728\uFF0C\u5E76\u4E14\u6E34\u671B\u7740\u4ED6\u5374\u8BA4\u4E3A\u81EA\u5DF1\u6C38\u8FDC\u4E5F\u5F97\u4E0D\u5230\u3002</p><ul><li>\u7B2C26\u7AE0 \u91CD\u590D\u4E0E\u6539\u53D8\uFF1A \u8D70\u51FA\u4E0D\u5B89\u5168\u578B\u4F9D\u604B\u3002</li></ul><p>\u672C\u7AE0\u8BB2\u8FF0\u4E86\u6210\u4EBA\u4F9D\u604B\u7C7B\u578B\u4E3B\u8981\u53D7\u7AE5\u5E74\u5F71\u54CD\u3002\u6253\u7834\u611F\u60C5\u4F24\u5BB3\u5FAA\u73AF\u7684\u5173\u952E\u9700\u8981\u4EE5\u4E0B\u4E09\u70B9\uFF1A</p><ol><li>\u6B63\u89C6\u95EE\u9898\uFF0C\u6446\u8131\u7F9E\u803B\u611F</li><li>\u62E5\u6709\u66FF\u4EE3\u699C\u6837\u6216\u5BFB\u6C42\u5FC3\u7406\u6CBB\u7597\uFF0C\u8FDB\u884C\u5185\u7701\uFF0C\u4E0E\u8FC7\u53BB\u548C\u89E3</li><li>\u4E0E\u652F\u6301\u7684\u4F34\u4FA3\u5EFA\u7ACB\u7A33\u5B9A\u5173\u7CFB</li></ol><p>\u6210\u4EBA\u4F9D\u604B\u7814\u7A76\u6307\u51FA\uFF0C\u7AE5\u5E74\u7ECF\u5386\u65E0\u6CD5\u6539\u53D8\uFF0C\u4F46\u6211\u4EEC\u53EF\u4EE5\u6446\u8131\u9632\u5FA1\u6027\u59FF\u6001\uFF0C\u7406\u89E3\u538B\u6291\u7684\u5F80\u4E8B\uFF0C\u5E76\u7528\u65B0\u89C6\u89D2\u63A5\u7EB3\u7AE5\u5E74\u7684\u81EA\u5DF1\u3002<br> \u6211\u4EEC\u53EF\u4EE5\u91CD\u65B0\u4F53\u9A8C\u88AB\u9694\u7EDD\u7684\u611F\u53D7\uFF0C\u5BF9\u5931\u53BB\u7684\u4E1C\u897F\u5B8C\u6210\u54C0\u60BC\u3002<br> \u901A\u8FC7\u6539\u53D8\u5185\u5728\u5DE5\u4F5C\u6A21\u578B\uFF0C\u6211\u4EEC\u53EF\u80FD\u5728\u5A5A\u59FB\u6216\u7C7B\u4F3C\u5173\u7CFB\u4E2D\u8D70\u51FA\u7AE5\u5E74\u9634\u5F71\u3002<br> \u82E5\u8FC7\u53BB\u7684\u5F71\u54CD\u8FC7\u4E8E\u5F3A\u5927\uFF0C\u53EF\u4EE5\u5BFB\u6C42\u5FC3\u7406\u6CBB\u7597\uFF0C\u4FDD\u6301\u5BF9\u65E9\u671F\u5185\u5728\u6A21\u578B\u7684\u6E05\u9192\u8BA4\u8BC6\u3002<br> \u8FD9\u6837\uFF0C\u6211\u4EEC\u66F4\u53EF\u80FD\u521B\u9020\u6EE1\u610F\u7684\u4F34\u4FA3\u5173\u7CFB\u548C\u5065\u5EB7\u7684\u4EB2\u5B50\u5173\u7CFB\u3002<br> \u4EBA\u7C7B\u7684\u60C5\u7EEA\u4E16\u754C\u4E2D\uFF0C\u90A3\u4E9B\u6211\u4EEC\u8BB0\u4E0D\u8D77\u3001\u770B\u4E0D\u6E05\u3001\u653E\u4E0D\u4E0B\u7684\u4E1C\u897F\u6CE8\u5B9A\u4F1A\u91CD\u6F14\u3002</p><h2 id="\u7B2C\u516D\u90E8\u5206-\u4E00\u9879\u7406\u5FF5\u7684\u957F\u5F81" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516D\u90E8\u5206-\u4E00\u9879\u7406\u5FF5\u7684\u957F\u5F81" aria-hidden="true">#</a> \u7B2C\u516D\u90E8\u5206 \u4E00\u9879\u7406\u5FF5\u7684\u957F\u5F81</h2><ul><li>\u7B2C27\u7AE0 \u56DE\u907F\u578B\u793E\u4F1A\uFF1A \u7126\u8651\u578B\u4F9D\u604B\u7684\u6587\u5316\u6839\u57FA</li></ul><p>\u672C\u7AE0\u4ECE\u793E\u4F1A\u751F\u4EA7\u5173\u7CFB\u548C\u5BB6\u5EAD\u7ED3\u6784\u51FA\u53D1, \u4ECE\u793E\u4F1A\u5B66\u89D2\u5EA6\u8BB2\u8FF0\u4E86\u73B0\u4EE3\u793E\u4F1A\u4F9D\u604B\u7C7B\u578B\u53D1\u5C55\u56F0\u5883\u3002\u4EE5\u53CA\u4E2A\u4EBA\u4E0E\u653F\u5E9C\u76F8\u5E94\u7684\u5E94\u5BF9\u529E\u6CD5\u3002</p><ul><li>\u7B2C28\u7AE0 \u56DE\u671B\uFF1A \u9C8D\u5C14\u6BD4\u548C\u5B89\u65AF\u6C83\u65AF</li></ul><p>\u672C\u7AE0\u4E3B\u8981\u8BB2\u8FF0\u4E86\u9C8D\u5C14\u6BD4\u548C\u5B89\u65AF\u6C83\u65AF\u7B49\u5B66\u8005\u5BF9\u4E8E\u4F9D\u604B\u7406\u8BBA\u7684\u8D21\u732E\u3002\u540C\u65F6\u590D\u76D8\u4E86\u4F9D\u604B\u7406\u8BBA\u7684\u53D1\u5C55\u8F68\u8FF9\uFF0C\u9664\u4E86\u4F9D\u604B\u7406\u8BBA\u5BF9\u4E8E\u793E\u4F1A\u7684\u8D21\u732E\uFF0C\u5C55\u671B\u4E86\u672A\u6765\u9700\u8981\u5B8C\u5584\u7684\u65B9\u9762\u3002</p><p>\u7C7B\u4F3C\u4E66\u7C4D\u63A8\u8350<a href="#%E5%A4%A9%E7%94%9F%E9%9D%9E%E6%AD%A4">\u5929\u751F\u975E\u6B64</a></p>',72),e=[u];function h(d,b){return i(),r("div",null,e)}const t=l(a,[["render",h],["__file","\u4F9D\u604B\u7684\u5F62\u6210.html.vue"]]);export{t as default};
